import React, {useState} from 'react'
import Base from '../core/Base'
import {Link, Redirect} from 'react-router-dom'
//importing index.js from auth/helper
import {signin, authenticate, isAuthenticated} from "../auth/helper"

const Signin = () => {
    const [values, setValues] = useState({
        email : "",
        password : "",
        error : "",
        loading : false, //for loading message
        //redirect the user to user panel or admin panel based on their role, it is going to come from 'react-router-dom'
        didRedirect : false
    })

    const {email, password, error, loading, didRedirect} = values
    //isAuthenticated() returns JSON.parse entire local storage object which has this jwt, so we are holding the things that are returned in a variab
    const {user} = isAuthenticated()



    const signInForm = () => {
        return(
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <form>
                        <div className="form-group">
                            <label className="text-light">Email</label>
                            <input className="form-control" type="email"/>
                        </div>
                        <div className="form-group">
                            <label className="text-light">Password</label>
                            <input className="form-control" type="password"/>
                        </div>
                        <button className="btn btn-success btn-block">Submit</button>
                    </form>
                </div>
            </div>
        )
    }

    return(
        <Base title="SignIn Page" description="A page for user to SignIn!">
        {signInForm()}
        </Base>
    )
}

export default Signin;
