import React, {useState, useEffect} from 'react'
import '../styles.css'
import {API} from "../backend"
import Base from './Base'
import Card from './Card'
import { loadCart } from './cartHelper'


const Cart = () => {
    const [products, setProducts] = useState([])
    
    const loadAllProducts = () => {
        return(
            <div>
            <h2>This section is to load products</h2>
            {products.map((product, index) => (
              <Card
                key={index}
                product={product}
                removeFromCart={true} //this ll allow to see remove fro
                addtoCart={false}
              />
            ))}
          </div>
        )
    }

    const loadCheckout = () => {
        return(
            <div>
                <h2>This section is for Checkout</h2>
            </div>
        )
    }


    return (
        <Base title="Cart Page" description="Ready To Checkout"> {/* now title in Base.js is replaced by this title only for Home.js, this is reuse of components */}
            {/* <h1 className="text-white">Hello Frontend</h1>   This becomes children */}
            <div className = "row">
                <div className="col-6">{loadAllProducts()}</div>
                <div className="col-6">{loadCheckout()}</div>
            </div>
        </Base>
    )
}

export default Cart
